	public void setup(Context context) throws IOException{
		files = DistributedCache.getCacheFiles(context.getConfiguration());
		
		
		Path path = new Path(files[0]);
		FileSystem fs = FileSystem.get(context.getConfiguration());
		FSDataInputStream fis = fs.open(path);
		BufferedReader br = new BufferedReader(new InputStreamReader(fis));
		String line="";
		
		while((line = br.readLine()) !=null) {
			String splits[]=line.split("\t");
			AFINN_map.put(splits[0], splits[1]);
			
			
	    }
		br.close();
		fis.close();
		
	}
	
	
	@Override
	protected void map(Object key, Text value, Context context) throws IOException, InterruptedException{

		 String line = value.toString();
		 String data[] = line.split(":");
//		 System.out.println(data[0].toString() + " "+ data[1].toString() + " "+data[2]);
		 if(data.length == 4) {
		 String productId = data[1].toString();
		 String customerId = data[0].toString();
		 String reviews = data[3];
		 String rating = data[2];
		 
		 int sentimentSum = 0;
		 String words[]=reviews.split(" ");
		 
		 if(value.toString().contains("customer_id")) {
			 
		 }
		 else {
			 for(String word:words) {
				 if(word.length()>1 && AFINN_map.containsKey(word)) {
					 Integer s = new Integer(AFINN_map.get(word));
						sentimentSum += s;
				 }
			 }
		 }
		 
		 context.write(new Text(productId + "\t" +customerId + "\t" + rating), new IntWritable(sentimentSum));
		 }
	}
	


